import layout from"./layout.js";export default class extends layout{constructor(e){super(e),this.setTitle("Board")}async getHTML(){return'\n            <div class="board">   \n                <div id="myProgress">\n                    <div id="myBar" style = "width : 66.66%"></div>\n                </div>\n                <div class="board__content">   \n                    <div class="board__calculator-unit">\n                        <h1> 평 을 제곱미터로 바꾸기 </h1>\n                        <label for="length-pyeong">평 수 입력하기 : </label>\n                        <input type="number" id="autoInput" placeholder = "예 ) 25평이면 25 입력"><br>\n                        \n                        <div class = "board__calculator-unit-result"></div>\n                        \n                        <span>소숫점 3 자리에서 반올림한 결과입니다.</span>\n                    </div>\n                    <div class="board__calculator-price">\n                        \n                        <h1> 공간의 규모를 알려주세요 </h1>\n                        \n                        <div class = "board__calculator-flex">\n                            <div class = "board__calculator-left">\n                                <label for="length">공간의 가로 길이 입력하기 : </label>\n                                <input type="number" id="width" name="length" placeholder = "제곱미터로 입력하기"><br>\n                            \n                                <label for="width">공간의 세로 길이 입력하기 : </label>\n                                <input type="number" id="length" name="width" placeholder = "예 ) 25㎡이면 25 입력"><br>\n                            </div>\n                            <div class = "board__calculator-line"></div>\n\n                            <div class = "board__calculator-right">\n                                <label for="scale">공간의 전체 면적 입력하기 : </label>\n                                <input type="number" id="scale" name="scale" placeholder = "숫자만 입력해주세요"><br>\n                            </div>\n                        </div>\n                        <div class="board__amountOfimgs">\n                            <label for="amount">필요한 이미지 수량 입력하기 :</label>\n                            <input type="number" id="amount" name="amount" placeholder = "숫자만 입력해주세요"><br>\n        \n                            <div class = "board__btn-wrap">\n                                <button class = "board__caculate-btn">계산하기</button><br>\n                            </div>\n                        </div>\n                        <label for="result">예상 견적 금액은 : </label>\n                        <input type="text" id="result" name="result" disabled>\n                    </div>\n                    <div class="board__deadline">\n                        <h1> 언제까지 필요한지 알려주세요 </h1>\n                        <label for="deadline">날짜 입력하기 : </label>\n                        <input type="date" id="deadline" name="deadline"><br>\n                    </div>\n                    <div class="board__btn-flex">\n                        <div class="board__btn-wrap">\n                            <a href="/design" data-link id = "next-btn"> 이전 페이지 </a>\n                        </div>\n                        <div>\n                            <p>\n                                <a href="/board" data-link class="reset-btn"> 다시 입력하기 </a>\n                            </p>\n                        </div>\n                        <div class="board__btn-wrap">\n                            <a href="/submit" data-link id = "next-btn"> 다음 페이지 </a>\n                        </div>\n                    </div>\n                </div>\n            </div>\n        '}executeScript(){console.log("Hello This is Board Page");const e=document.getElementById("autoInput"),t=document.querySelector(".board__calculator-unit-result");e.addEventListener("input",(function(){const n=parseFloat(e.value);if(isNaN(n))t.textContent="숫자를 입력하세요.";else{const e=(3.3058*n).toFixed(2)+"제곱미터 입니다.";t.textContent=e}})),document.querySelector(".board__caculate-btn").addEventListener("click",(function(){const e=document.getElementById("scale").value,t=document.getElementById("amount").value,n=document.getElementById("length").value,a=document.getElementById("width").value;console.log(t);let l=0;""===e?(l=n*a*t/33.0579,localStorage.setItem("length",n),localStorage.setItem("width",a),localStorage.setItem("amount",t)):""!==a&&""!==n||(l=e*t/33.0579,localStorage.setItem("scale",e),localStorage.setItem("amount",t));const d=(1e3*Math.round(5e4*l/1e3)).toString().replace(/\B(?=(\d{3})+(?!\d))/g,","),o=document.getElementById("result");o.value=d+" 원 입니다.",console.log(o.value),localStorage.setItem("result",o.value)}));const n=document.getElementById("deadline"),a=(new Date).toISOString().split("T")[0];n.setAttribute("min",a);const l=document.getElementById("deadline");l.addEventListener("input",(function(){const e=l.value;localStorage.setItem("deadline",e)}))}}